mapper.select:
select *,u.nickname,u.signature from post p join user u on p.user_id=u.id
join favorite f on p.user_id=f.concerned

    id = #{id}
    and content = #{content}
    and user_id = #{userId}
    and publishTime = #{publishTime}
    and good_nums = #{goodNums}
    and view_nums = #{viewNums}
    and p.is_deleted = #{isDeleted}
    and f.user_id = #{currentUserId}

        and (u.campus1 = #{campus}
        OR campus2 = #{campus}
        OR campus3 = #{campus}
        OR campus4 = #{campus}
        OR campus5 = #{campus}
        OR campus6 = #{campus})


order by p.id desc
